import React, { useState, useEffect } from "react";
import "../styles/admin.css";

const MonthlyReceipt = () => {
  const [transactions, setTransactions] = useState([]);
  const [itemName, setItemName] = useState("");
  const [cost, setCost] = useState("");
  const [type, setType] = useState("revenue");
  const [userId, setUserId] = useState("");
  const [message, setMessage] = useState("");
  const [notifications, setNotifications] = useState([]);

  useEffect(() => {
    const fetchExpenses = async () => {
      const data = await fetch("http://localhost:5000/getexpenses",{
          method: "GET",
          headers: {
            "Content-Type": "application/json",
          },
        });
    
    }
    fetchExpenses();
  }, []);


  return (
    
    <div className="receipt-container">
      <h1 className="title">Club Finances Admin Dashboard</h1>
      <h1>Monthly Transactions</h1>
      <div className="add-transaction-form">
        <input
          type="text"
          value={itemName}
          onChange={(e) => setItemName(e.target.value)}
          placeholder="Item Name"
        />
        <input
          type="number"
          value={cost}
          onChange={(e) => setCost(e.target.value)}
          placeholder="Cost"
        />
        <select value={type} onChange={(e) => setType(e.target.value)}>
          <option value="revenue">Revenue</option>
          <option value="expense">Expense</option>
        </select>
        <button>Add Transaction</button>
      </div>

      <div className="tabletrans">
        <table className="transaction-table">
          <thead>
            <tr>
              <th>Date</th>
              <th>Type</th>
              <th>Source/Category</th>
              <th>Amount</th>
            </tr>
          </thead>
          <tbody>
            {}
          </tbody>
        </table>
      </div>

      <div className="income-statement">
        <h3>Total Revenue: ${}</h3>
        <h3>Total Expenses: ${}</h3>
        <h3>Profit: ${}</h3>
      </div>
      
    </div>
  );
};

export default MonthlyReceipt;
