import React, { useState, useEffect } from "react";
import "../styles/admin.css";

const MonthlyReceipt = () => {
  const [transactions, setTransactions] = useState([]);
  const [itemName, setItemName] = useState("");
  const [cost, setCost] = useState("");
  const [type, setType] = useState("revenue");
  const [incomeStatement, setIncomeStatement] = useState({
    total_revenue: 0,
    total_expenses: 0,
    profit: 0,
  });
  const [userId, setUserId] = useState("");
  const [message, setMessage] = useState("");
  const [sentMessages, setSentMessages] = useState([]);
  const [receivedMessages, setReceivedMessages] = useState([]);

  useEffect(() => {
    fetchIncomeStatement();
    fetchTransactions();
    fetchMessages();
  }, []);

  const fetchIncomeStatement = () => {
    fetch("http://localhost:5000/income_statement")
      .then((response) => response.json())
      .then((data) => {
        setIncomeStatement(data);
      })
      .catch((error) =>
        console.error("Error fetching income statement:", error)
      );
  };

  const fetchTransactions = () => {
    Promise.all([
      fetch("http://localhost:5000/api/revenues"),
      fetch("http://localhost:5000/api/expenses"),
    ])
      .then(([revenueResponse, expenseResponse]) =>
        Promise.all([revenueResponse.json(), expenseResponse.json()])
      )
      .then(([revenueData, expenseData]) => {
        const unifiedTransactions = [
          ...revenueData.map((item) => ({
            ...item,
            type: "Revenue",
            category: item.source,
          })),
          ...expenseData.map((item) => ({
            ...item,
            type: "Expense",
            category: item.category,
          })),
        ];
        setTransactions(unifiedTransactions);
      })
      .catch((error) => console.error("Error fetching transactions:", error));
  };

  const fetchMessages = () => {
    const userId = localStorage.getItem("userId"); // Assume user ID is stored in localStorage
    if (!userId) return;
    const sentMessagesUrl = `http://localhost:5000/api/messages/sent?userId=${userId}`;
    const receivedMessagesUrl = `http://localhost:5000/api/messages/received?userId=${userId}`;

    Promise.all([
      fetch(sentMessagesUrl).then((res) => res.json()),
      fetch(receivedMessagesUrl).then((res) => res.json()),
    ])
      .then(([sentData, receivedData]) => {
        setSentMessages(sentData);
        setReceivedMessages(receivedData);
      })
      .catch((error) => console.error("Error fetching messages:", error));
  };

  const sendMessage = (msgType) => {
    const notification = {
      userId,
      message:
        msgType === "custom"
          ? message
          : msgType === "lesson"
          ? "Your lesson fee is unpaid."
          : "Your monthly fee is unpaid.",
    };

    fetch("http://localhost:5000/send_message", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(notification),
    })
      .then((response) => response.json())
      .then((data) => {
        fetchMessages(); // Refresh the list of messages after sending one
        setMessage(""); // Clear the message input after sending
        setUserId(""); // Clear the user ID input after sending
      })
      .catch((error) => console.error("Error sending message:", error));
  };

  return (
    <div className="receipt-container">
      <h1>Monthly Transactions</h1>
      <div className="add-transaction-form">
        <input
          type="text"
          value={itemName}
          onChange={(e) => setItemName(e.target.value)}
          placeholder="Item Name"
        />
        <input
          type="number"
          value={cost}
          onChange={(e) => setCost(e.target.value)}
          placeholder="Cost"
        />
        <select value={type} onChange={(e) => setType(e.target.value)}>
          <option value="revenue">Revenue</option>
          <option value="expense">Expense</option>
        </select>
        <button onClick={addTransaction}>Add Transaction</button>
      </div>

      <div className="tabletrans">
        <table className="transaction-table">
          <thead>
            <tr>
              <th>Date</th>
              <th>Type</th>
              <th>Source/Category</th>
              <th>Amount</th>
            </tr>
          </thead>
          <tbody>
            {transactions.map((transaction, index) => (
              <tr key={index}>
                <td>{transaction.date}</td>
                <td>{transaction.type}</td>
                <td>{transaction.category}</td>
                <td>${transaction.amount}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="income-statement">
        <p>Total Revenue: ${incomeStatement.total_revenue}</p>
        <p>Total Expenses: ${incomeStatement.total_expenses}</p>
        <p>Profit: ${incomeStatement.profit}</p>
      </div>

      <div className="messaging-section">
        <h2>Send Message</h2>
        <input
          type="text"
          value={userId}
          onChange={(e) => setUserId(e.target.value)}
          placeholder="User ID"
        />
        <textarea
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Write your message here"
        />
        <button onClick={() => sendMessage("custom")}>
          Send Custom Message
        </button>
        <button onClick={() => sendMessage("lesson")}>
          Send Unpaid Lesson Fee Message
        </button>
        <button onClick={() => sendMessage("monthly")}>
          Send Unpaid Monthly Fee Message
        </button>
      </div>

      <div className="sent-received-messages">
        <h2>Sent Messages</h2>
        <ul>
          {sentMessages.map((msg, index) => (
            <li key={index}>
              {msg.date}: {msg.message}
            </li>
          ))}
        </ul>
        <h2>Received Messages</h2>
        <ul>
          {receivedMessages.map((msg, index) => (
            <li key={index}>
              {msg.date}: {msg.message}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default MonthlyReceipt;
