import "./styles/member.css";
import { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";

const Member = (props) => {
  const [latefee, setLatefee] = useState(0);
  const [tableStr, setTableStr] = useState("");
  const [tableStr2, setTableStr2] = useState("");
  const location = useLocation();
  const { username, fullname } = location.state || {};
  const { _username, _fullname } = props;

  useEffect(() => {
    const fetchDataAndBuildTable = async () => {
      try {
        console.log("called 1 time");
        const response = await fetch("http://localhost:5000/getpayments", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ fullname: fullname }),
        });
        const data = await response.json();
        console.log("response received");

        if (data.payments && data.payments.length > 0) {
          let latefee = data.payments[0].latefee;
          setLatefee(latefee);
          let title = "";
          let tempTableStr = "";

          data.payments.forEach((payment) => {
            let name = payment.fullname;
            let amount = payment.amount;
            let date = payment.date;
            if (amount == 10) {
              title = " - Session Payment";
            } else if (amount == 40) {
              title = " - Monthly payment";
            }
            tempTableStr += `
              <tr>
                <td>${name}${title}</td>
                <td>${date}</td>
                <td>${amount}</td>
              </tr>
            `;
          });

          setTableStr(tempTableStr);
        } else {
          console.log("No payments data received");
        }
      } catch (error) {
        console.error("Error:", error);
      }
      try {
        console.log("called 1 time");
        const response = await fetch("http://localhost:5000/getattendance", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ fullname: fullname }),
        });
        const data = await response.json();
        console.log("response received");

        if (data.payments && data.payments.length > 0) {
          let latefee = data.payments[0].latefee;
          setLatefee(latefee);
          let title = "";
          let tempTableStr = "";

          data.payments.forEach((payment) => {
            let name = payment.fullname;
            let amount = payment.amount;
            let date = payment.date;
            if (amount == 10) {
              title = " - Session Payment";
            } else if (amount == 40) {
              title = " - Monthly payment";
            }
            tempTableStr += `
              <tr>
                <td>${name}${title}</td>
                <td>${date}</td>
                <td>${amount}</td>
              </tr>
            `;
          });

          setTableStr(tempTableStr);
        } else {
          console.log("No payments data received");
        }
      } catch (error) {
        console.error("Error:", error);
      }
    };

    fetchDataAndBuildTable();
  }, [fullname]); // Include fullname in the dependency array to trigger useEffect on fullname change

  return (
    <div className="mainContainer">
      <div>
        <div className="dashboard">
          <div className="left-side">
            <div className="left-item" id="leftItem1">
              <h1>Welcome, {fullname}</h1>
              <h2>Club Member</h2>
              <h3>User ID: {username}</h3>
              <h3>Your Bill: $10/Session</h3>
              <h3>
                Late Fees ($20 per unpaid bill):{" "}
                <span style={{ color: latefee > 0 ? "red" : "black" }}>
                  ${latefee}
                </span>
              </h3>
              <span className="warning">
                *Failure to pay late fees will result in an indefinite
                suspension from all club activities.
              </span>
            </div>
            <div className="left-item2" id="leftItem2">
              <h2>Payment History</h2>
              <table>
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Date</th>
                    <th>Amount</th>
                  </tr>
                </thead>
                {tableStr && (
                  <tbody dangerouslySetInnerHTML={{ __html: tableStr }}></tbody>
                )}
              </table>
            </div>
          </div>
          <div className="right-side">
            <div className="right-item" id="rightItem">
              <h2>Attendance History</h2>
              <table>
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Date</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td>John Doe</td>
                    <td>2024-04-15</td>
                  </tr>
                  <tr>
                    <td>Jane Smith</td>
                    <td>2024-04-16</td>
                  </tr>
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Member;
