import "./styles/member.css";
import { useEffect, useState } from "react"; // Import useState and useEffect if needed
import { useLocation } from 'react-router-dom';

const Member = (props) => {

const location = useLocation();
const { username, fullname } = location.state || {};
  const { _username, _fullname} = props;
  let latefees = 0;

  useEffect(() => {
    const fetchData = async () => {
      // Fetch data or perform other actions when component mounts or route changes
      // Example code from your original onLoad function
      try {
        const response = await fetch("http://localhost:5000/getpayments", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ username: username, password: password }),
        });
        const data = await response.json();
        
        if (data.valid) {
          if (data.isAdmin) {
            navigate("/admin", { state: { username: username, fullname: data.fullname } });
          } else {
            navigate("/member", { state: { username: username, fullname: data.fullname } });
          }
        } else {
          const loginfail = document.getElementById("loginfail");
          loginfail.style.display = "block";
        }
      } catch (error) {
        const loginfail = document.getElementById("loginfail");
        loginfail.style.display = "block";
        console.error("Error:", error);
      }
    };

    fetchData(); // Call fetchData when component mounts or route changes
  }, [navigate]); // Dependency array ensures effect runs when navigate changes

  async function onLoad() {
    try {
      const response = await fetch("http://localhost:5000/validate", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ username: username, password: password }),
      });
      const data = await response.json();
      
      if (data.valid) {
        if (data.isAdmin) {
          navigate("/admin", { state: { username: username, fullname: data.fullname } });
        } else {
          navigate("/member", { state: { username: username, fullname: data.fullname } });
        }
      } else {
        const loginfail = document.getElementById("loginfail");
        loginfail.style.display = "block";
      }
    } catch (error) {
      const loginfail = document.getElementById("loginfail");
      loginfail.style.display = "block";
      console.error("Error:", error);
    }
  }

  return (
    <div className="mainContainer">
      <div>
        <div className="dashboard">
          <div className="left-side">
            <div className="left-item" id="leftItem1">
              <h1>Welcome, {fullname}</h1>
              <h2>Club Member</h2>
              <h3>User ID: {username}</h3>
              <h3>Your Bill: $10/Session</h3>
              <h3>
                Late Fees ($20 per unpaid bill):{" "}
                <span style={{ color: latefees > 0 ? "red" : "black" }}>
                  ${latefees} 
                </span>
              </h3>
              <span className="warning" style={{ color: latefees > 0 ? "red" : "black" }}>*Failure to pay late fees will result in an indefinate suspension from all club activities.</span>
            </div>
            <div className="left-item2" id="leftItem2">
              <h2>Payment History</h2>
              <table>
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Date</th>
                    <th>Amount</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td>{fullname.split(" ")[1]} - </td>
                    <td>2024-04-15</td>
                    <td>$100.00</td>
                  </tr>
                  <tr>
                    <td>Jane Smith</td>
                    <td>2024-04-16</td>
                    <td>$150.00</td>
                  </tr>
                </tbody>
              </table>
            </div>
          </div>
          <div className="right-side">
            <div className="right-item" id="rightItem">
              <h2>Attendance History</h2>
              <table>
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Date</th>
        
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td>John Doe</td>
                    <td>2024-04-15</td>
                   
                  </tr>
                  <tr>
                    <td>Jane Smith</td>
                    <td>2024-04-16</td>
                  </tr>
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Member;
